+                    setDocSingleDate={setDocSingleDate}
+                    setEditNameForm={setEditNameForm}
+                    ShowDeleteAlert={ShowDeleteAlert}
+                    setDocPreview={setDocPreview}
+                    handleBulkFileChange={handleBulkFileChange}
+                    saveBtn={saveBtn}
+                    loadingBtn={loadingBtn}
